    private int[][][] worldArray = {map1};
    private int[][][] fireBallArray = {fireBallMap1};
    private int[][][] enemyArray = {enemyMap1};

    /**
     * Constructor for objects of class MyWorld.
     *
     */
    public Level(int player) {
        // Create a new world with 600x400 cells with a cell size of 1x1 pixels.
        super(1000, 800, 1, false);
        this.setBackground("bg.png");
        this.player = player;

        // Declareren en initialiseren van de TileEngine klasse om de map aan de world toe te voegen
        TileEngine te = new TileEngine(this, 70, 70, worldArray[0]);
        // Declarenre en initialiseren van de camera klasse met de TileEngine klasse 
        // zodat de camera weet welke tiles allemaal moeten meebewegen met de camera
        Camera camera = new Camera(te);
        // Declareren en initialiseren van een main karakter van het spel mijne heet Hero. Deze klasse 
        // moet de klasse Mover extenden voor de camera om te werken
        int heroSpawnX = 300;
        int heroSpawnY = 1800;
        overlay = new Overlay(player);
        hero = new Hero(player, heroSpawnX, heroSpawnY, overlay);
        addObject(overlay, 0, 0);

        // Laat de camera een object volgen. Die moet een Mover instatie zijn of een extentie hiervan.
        camera.follow(hero);

        // Alle objecten toevoegen aan de wereld: camera, main karakter en mogelijke enemies
        addObject(camera, 0, 0);
        addObject(hero, heroSpawnX, heroSpawnY);
        for (int i = 0; i < enemyArray[0].length; i++) {
            addObject(new Enemy(150), enemyArray[0][i][0], enemyArray[0][i][1]);
        }
        for (int i = 0; i < fireBallArray[0].length; i++) {
            addObject(new FireBall(), fireBallArray[0][i][0], fireBallArray[0][i][1]);
        }

        // Force act zodat de camera op de juist plek staat.
        camera.act();
        hero.act();

        // Initialiseren van de CollisionEngine zodat de speler niet door de tile heen kan lopen.
        // De collision engine kijkt alleen naar de tiles die de variabele solid op true hebben staan.
        ce = new CollisionEngine(te, camera);
        // Toevoegen van de mover instantie of een extentie hiervan
        ce.addCollidingMover(hero);
    }

    public Level(int player, int map, int heroSpawnX, int heroSpawnY) {

        super(1000, 800, 1, false);
        this.setBackground("bg.png");
        this.player = player;
        // Declareren en initialiseren van de TileEngine klasse om de map aan de world toe te voegen
        TileEngine te = new TileEngine(this, 70, 70, worldArray[map]);
        // Declarenre en initialiseren van de camera klasse met de TileEngine klasse 
        // zodat de camera weet welke tiles allemaal moeten meebewegen met de camera
        Camera camera = new Camera(te);
        // Declareren en initialiseren van een main karakter van het spel mijne heet Hero. Deze klasse 
        // moet de klasse Mover extenden voor de camera om te werken

        hero.setSpawn(heroSpawnX, heroSpawnY);
        addObject(overlay, 0, 0);

        // Laat de camera een object volgen. Die moet een Mover instatie zijn of een extentie hiervan.
        camera.follow(hero);

        // Alle objecten toevoegen aan de wereld: camera, main karakter en mogelijke enemies
        addObject(camera, 0, 0);
        addObject(hero, heroSpawnX, heroSpawnY);
        for (int i = 0; i < enemyArray[map].length; i++) {
            addObject(new Enemy(150), enemyArray[map][i][0], enemyArray[0][i][1]);
        }
        for (int i = 0; i < fireBallArray[map].length; i++) {
            addObject(new FireBall(), fireBallArray[map][i][0], fireBallArray[0][i][1]);
        }
        // Force act zodat de camera op de juist plek staat.
        camera.act();
        hero.act();

        // Initialiseren van de CollisionEngine zodat de speler niet door de tile heen kan lopen.
        // De collision engine kijkt alleen naar de tiles die de variabele solid op true hebben staan.
        ce = new CollisionEngine(te, camera);
        // Toevoegen van de mover instantie of een extentie hiervan
        ce.addCollidingMover(hero);
    }

    @Override
    public void act() {
        ce.update();
    }

